refsect3.programlisting:
#!/usr/bin/python3 #file try255.py
import sys import random import pyvips
# this makes a 8-bit, mono image of 100,000 x 100,000 pixels, each pixel zero im = pyvips.Image.black(100000, 100000)
for filename in sys.argv[2:]: tile = pyvips.Image.new_from_file(filename, access='sequential')
im = im.insert(tile, random.randint(0, im.width - tile.width), random.randint(0, im.height - tile.height))
im.write_to_file(sys.argv[1])
